Jetsetter.Models.UserGeo = Composer.Model.extend({
    init: function() {
        this.fetched = false;
        this.fetching = false;

        var lsCached = lscache.get('geo-data');
        if (lsCached && (lsCached != 'undefined')) {
            this.set({'data': lsCached});
            this.fetched = true;
            this.fetching = false;
        }
    },

    supply: function(callback) {
        if (this.fetched) {
            return callback();
        } else {
            this.bind_once('syncComplete', callback);
            if (!this.fetching) {
                this.fetch();
            }
        }
    },

    sync: function() {

        if (this.fetching) {
            // if the geo model is already fetching then don't re-send the request
            return true;
        }

        this.trigger('syncStart');

        this.fetched = false;
        this.fetching = true;

        this.request = new Request.JSON({
            url: '/geo',
            timeout: 500,
            onSuccess: function(res) {
                this.fetched = true;
                this.fetching = false;
                this.set({'data': res.data});
                lscache.set('geo-data', res.data, 86400 /* cache 1 day */)
                this.trigger('syncSuccess');
                this.trigger('syncComplete');
            }.bind(this),

            onFailure: function() {
                this.fetched = true;
                this.fetching = false;
                this.set({data: {}});
                this.trigger('syncFailure');
                this.trigger('syncComplete');
            }.bind(this),

            onTimeout: function() {
                this.fetched = true;
                this.fetching = false;
                this.set({data: {}});
                this.trigger('syncFailure');
                this.trigger('syncComplete');
            }.bind(this)
        }).get();
    }

});
