			yahooA = new Element('a', {'href': '#'}).inject(addToCalendarSecondRowDiv);
				new Element('img', {'class':'image', 'src': 'http://cdn.jetsetter.com/static/images/layout/icons_cals_yahoo.jpg', 'border': '0'}).inject(yahooA);
