	// disabled per JETSETTER-7177
	return false;

	var cookie = JSON.decode(Cookie.read('j_ipm'));

	if(cookie){
		var lastSeen = Date.parse(cookie.last_seen),
			count = cookie.count;
				
		if(count < 3 && lastSeen.diff(new Date(), 'day') > 7 && cookie.show){
			return true;
		}
		else{
			return false;
		}
	}
	else{
		return true;
	}
